import './App.css'
const App = ()=>{
  // 在React中，当组件渲染完毕后，在修改组件中的变量不会使组件重新渲染
  // 要使组件可以受到变量的影响，必须在变量修改后对组件进行重新渲染
  // 这里我们就需要一个特殊变量，当这个变量被修改时，组件会自动重新渲染
  let counter = 1
  const addHanlder = ()=>{
    counter++
    console.log(counter)
  }
  const lessHanlder = ()=>{
    counter--
  }
  return <div className="app">
    {/* 当点击+时,数字增大 */}
    <h1>{counter}</h1>
    <button onClick={addHanlder}>+</button>
    <button onClick={lessHanlder}>-</button>
  </div>
}
export default App
