import { Card } from "../UI/Card/Card"
import Logitem from "./LogItem"
import './Logs.css'
const Logs = (props)=>{
  /*当遇到一个组件需要多个组件使用时，我们可以将数据放入到这些组件共同的祖先元素中*/ 
  const logItemData = props.logsData.map((item,index) => <Logitem key={item.id} {...item} onDelLog={()=>{props.onDelLog(index)}}/>)
  return <Card className="logs">
    {/* {logsData.map(item => <Logitem date={item.date} desc={item.desc} time={item.time}/> )} */}
    {logItemData ? logItemData : <p>列表中没有找到日志</p>}
  </Card>
}
export default Logs
