import { Card } from '../UI/Card/Card'
import './LogsForm.css'

export const LogsForm = () => {
  let inputDate = ""
  let inputDesc = ""
  let inputTime = 0
  /*当表单项发生变化时，获取用户输入的内容*/
  // 创建一个响应函数，监听表单项的变化
  const dateChangeHanlder = (e) =>{
    inputDate = e.target.value
  }
  const timeChangeHanlder = (e) => {
    inputTime = e.target.value
  }

  const descChangeHanlder = (e)=>{
    // 事件对象中保存了当前事件触发时的所有信息
    // e.target 执行的是触发事件的对象（DOM对象）
    // console.log("desc发生了变化")
    // 获取到当前触发事件的对象
    inputDesc = e.target.value
  }
  // 在React中，通过表单不需要自行提交 而是要通过React提交
  const formSubmitHandler = (e)=>{
    e.preventDefault()
    console.log(inputDate,inputDesc,inputTime)
  }
  const newLog = {
    date: inputDate,
    desc: inputDesc,
    time: inputTime
  }
  return (
    <Card className = "logs-Form">
      <form>
        <div className='form-item'>
          <label htmlFor='date'>日期</label>
          <input id="date" type='date' onChange={dateChangeHanlder}></input>
        </div>
        <div className='form-item'>
          <label htmlFor='desc'>内容</label>
          <input onChange={descChangeHanlder} id="desc" type='text'></input>
        </div>
        <div className='form-item'>
          <label htmlFor='time'>时长</label>
          <input id="time" type='number' onChange={timeChangeHanlder}></input>
        </div>
        <div className='form-btn'>
          <button onClick={formSubmitHandler}>添加</button>
        </div>
      </form>
    </Card>
  )
}
