import './App.css'
const App = ()=>{
  let counter = 1
  const addHanlder = ()=>{
    counter++
  }
  const lessHanlder = ()=>{
    counter--
  }
  return <div className="app">
    {/* 当点击+时,数字增大 */}
    <h1>{counter}</h1>
    <button onClick={addHanlder}>+</button>
    <button onClick={lessHanlder}>-</button>
  </div>
}
export default App
