import './App.css'
const App = ()=>{
  const clickHandler = ()=>{
    const header = document.getElementById("header")
    header.innerHTML = "哈哈"
  }
  return <div className="app">
    {/* 当点击+时,数字增大 */}
    <h1 id='header'>我是标题</h1>
    <button onClick={clickHandler}>1</button>
    <button>2</button>
  </div>
}
export default App
